import { Modal } from '../UI/Modal/Modal'
import classes from './Cart.module.css'
import { useContext } from 'react';
import { CartContext } from '../../store/cart-context';
import CartItem from './CartItem'

export const Cart = (props) => {

    const cartCtx = useContext(CartContext)
    const totalAmount = `R$ ${cartCtx.totalAmount.toFixed(2)}`;
    const hasItems = cartCtx.items.length > 0;

    const cartItemRemoveHandler = (id) => {

    }

    const cartItemAddHandler = (item) => {

    }

    const cartItems = 
        <ul className={classes['cart-items']}>
            {cartCtx.items.map(item => {
                return <CartItem key={item.id} name={item.name} amount={item.amount} price={item.price} onRemove={cartItemRemoveHandler} onAdd={cartItemAddHandler} />
            })}
        </ul>

    return (
        <Modal onCloseCart={props.onCloseCart}>
            {cartItems}
            <div className={classes.total}>
                <span>Total Amount</span>
                <span>{ totalAmount }</span>
            </div>
            <div className={classes.actions}>
                <button onClick={props.onCloseCart} className={classes['button--alt']}>Close</button>
                { hasItems && <button className={classes.button}>Order</button> }
            </div>
        </Modal>
    )
}
